'use client'
import chapters from '@/../data/chapters.json'
import { ChapterImage } from './ChapterImage'
import { useCallback, useEffect, useState, type Dispatch, type SetStateAction } from 'react'
import { LinkNoPrefetch } from '@/components/LinkNoPrefetch'
import classNames from 'classnames'
import Image from 'next/image'
import { QUALITY, createImageUrl, loadPages } from './utils'

import { type ChapterPage } from './page'
import type { Chapter } from '@/../types'

export function ChapterContent({
  chapterIndex,
  nextPage,
  previousPage,
  pages,
}: {
  chapterIndex: number
  nextPage: number
  previousPage: number
  pages: ChapterPage[]
}) {
  const chapter = chapters[chapterIndex]
  const [currentPage, setCurrentPage] = useState(0)

  useEffect(() => {
    // TODO: Fix the width that's generated by Next/image
    if (process.env.VERCEL_ENV === 'production') {
      warmUpCacheForChapter(chapters[chapterIndex + 1])
      warmUpCacheForChapter(chapters[chapterIndex - 1])
    }
  })

  return (
    <>
      <h1 className="text-xl">{chapter.name}</h1>
      <div className="flex flex-col items-center -mx-4">
        <Navigation next={nextPage} previous={previousPage} />
        {pages.map((page, pageIndex) => {
          return (
            <ChapterImageController
              key={pageIndex}
              setCurrentPage={setCurrentPage}
              pageIndex={pageIndex}
              page={page}
              currentPage={currentPage}
            />
          )
        })}
        <Navigation next={nextPage} previous={previousPage} />
      </div>
    </>
  )
}

async function warmUpCacheForChapter(chapter: Chapter) {
  if (chapter) {
    const pages = await loadPages(chapter.number)
    return Promise.all(
      pages.map((page) =>
        fetch(
          createImageUrl({
            src: page.url,
            width: page.width,
            quality: QUALITY,
          })
        )
      )
    )
  }
}

function ChapterImageController({
  setCurrentPage,
  pageIndex,
  page,
  currentPage,
}: {
  setCurrentPage: Dispatch<SetStateAction<number>>
  pageIndex: number
  page: ChapterPage
  currentPage: number
}) {
  const handlePageEnterViewport = useCallback(() => {
    setCurrentPage((prevPageIndex) => (pageIndex > prevPageIndex ? pageIndex : prevPageIndex))
  }, [pageIndex, setCurrentPage])

  return (
    <ChapterImage
      url={page.url}
      width={page.width}
      height={page.height}
      pageNumber={pageIndex + 1}
      onEnterViewport={handlePageEnterViewport}
      priority={pageIndex <= currentPage + 2}
    />
  )
}

function Navigation({ previous, next }: { previous: number; next: number }) {
  return (
    <div className="my-2">
      <LinkNoPrefetch
        href={`/chapters/${previous}`}
        className={classNames({ invisible: previous == null })}>
        <button className="bg-slate-400 rounded-full p-3">
          <Image
            src="data:image/svg+xml;base64,"
            width="24"
            height="24"
            alt="prev"
          />
        </button>
      </LinkNoPrefetch>
      <LinkNoPrefetch
        href={`/chapters/${next}`}
        className={classNames({ invisible: next == null })}>
        <button className="bg-slate-400 rounded-full p-3 ml-10">
          <Image
            src="data:image/svg+xml;base64,"
            width="24"
            height="24"
            alt="prev"
          />
        </button>
      </LinkNoPrefetch>
    </div>
  )
}
